if Vs30>max(Vs)
    disp('Error, max Vas30 is 2824 m/s')
    return
elseif Vs30<min(Vs)
    disp('Error, min Vs30 is 95 m/s')
    return
end

% interpolation to frequency f and Vs30
AMP_int = interp2(log(Vs),log(FH),AMP.',log(Vs30),log(f));

